<template>
    <div class="p-6">
      <div class="flex justify-between items-center mb-4">
        <h2 class="text-2xl font-bold">Manager Product</h2>
        <button
          class="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
          @click="showForm = true"
        >
          Add New Product
        </button>
      </div>
  
      <AddProductForm v-if="showForm" @closeForm="showForm = false" @addProduct="addProduct" />
  
      <table class="min-w-full border border-gray-300 mt-4">
        <thead class="bg-gray-100">
          <tr>
            <th class="border border-gray-300 px-4 py-2">STT</th>
            <th class="border border-gray-300 px-4 py-2">Image</th>
            <th class="border border-gray-300 px-4 py-2">Name</th>
            <th class="border border-gray-300 px-4 py-2">Price</th>
            <th class="border border-gray-300 px-4 py-2">Quantity</th>
            <th class="border border-gray-300 px-4 py-2">Option</th>
          </tr>
        </thead>
        <tbody>
          <tr
            v-for="(product, index) in products"
            :key="index"
            class="border-t text-center"
          >
            <td class="border border-gray-300 px-4 py-2">{{ index + 1 }}</td>
            <td class="border border-gray-300 px-4 py-2">
              <img :src="product.image" alt="product.name" class="w-14 h-10 object-cover" />
            </td>
            <td class="border border-gray-300 px-4 py-2">{{ product.name }}</td>
            <td class="border border-gray-300 px-4 py-2">{{ formatCurrency(product.price) }}</td>
            <td class="border border-gray-300 px-4 py-2">{{ product.quantity }}</td>
            <td class="border border-gray-300 px-4 py-2">
                <button @click="editProduct(product, index)" class="bg-orange-500 text-white px-4 py-2 rounded mr-2">
              Edit
            </button>
            <button @click="confirmDeleteProduct(index)" class="bg-red-500 text-white px-4 py-2 rounded">Delete</button>
            </td>
          </tr>
        </tbody>
      </table>

    <EditProductForm
      v-if="showEditForm"
      :product="selectedProduct"
      @closeForm="closeEditForm"
      @updateProduct="updateProduct"
    />
      
    <ConfirmDeleteModal
      v-if="showConfirmDelete"
      @confirm="deleteProduct"
      @cancel="closeConfirmDelete"
    />

    </div>
  </template>
  
  <script setup>
    import {ref} from "vue";
    import AddProductForm from './AddProductForm.vue';
    import EditProductForm from './EditProductForm.vue';
    import ConfirmDeleteModal from './ConfirmDeleteModal.vue';

    const products = ref(JSON.parse(localStorage.getItem("products")) || []);
    const showForm = ref(false);
    const showEditForm = ref(false);
    const selectedProduct = ref(null);
    let selectedProductIndex = -1;
    const showConfirmDelete = ref(false);
    let productToDeleteIndex = -1;

    const addProduct = (newProduct) => {
        products.value.push(newProduct);
        localStorage.setItem('products', JSON.stringify(products.value));
        showForm.value = false;
    };

    const editProduct = (product, index) => {
        selectedProduct.value = { ...product };
        selectedProductIndex = index;
        showEditForm.value = true;
    };

    const closeEditForm = () => {
        showEditForm.value = false;
    };

    const updateProduct = (updatedProduct) => {
        products.value[selectedProductIndex] = updatedProduct;
        localStorage.setItem('products', JSON.stringify(products.value));
        showEditForm.value = false;
    };

    const confirmDeleteProduct = (index) => {
        productToDeleteIndex = index;
        showConfirmDelete.value = true;
    };

    const closeConfirmDelete = () => {
        showConfirmDelete.value = false;
    };

    const deleteProduct = () => {
        products.value.splice(productToDeleteIndex, 1);
        localStorage.setItem('products', JSON.stringify(products.value));
        showConfirmDelete.value = false;
        productToDeleteIndex = -1;
    };

    const formatCurrency = (price) => {
        return new Intl.NumberFormat('vi-VN', {
        style: 'currency',
        currency: 'VND',
    }).format(price);
};


